const Header = (props)=>{
return (<h1>{props.course}</h1>);
}

const Part = (props)=>{
  return (
     <p>
        {props.part} {props.exercise}
    </p>
  );
  }


const Content = (props)=>{
  return (
    <div>
    <Part part={props.parts[0].name} exercise={props.parts[0].exercises}/>
    <Part part={props.parts[1].name} exercise={props.parts[1].exercises}/>
    <Part part={props.parts[2].name} exercise={props.exercises}/>
    </div>
  );
  }

  const Total = (props) =>{
    return (
      <p>Number of exercises {props.exercises1 + props.exercises2+ props.exercises3}</p>
    )
  }



const App = () => {
  const course = 'Half Stack application development'
  const parts = [{
    name: 'Fundamentals of React',
    exercises: 10
  },
  {
    name: 'Using props to pass data',
    exercises: 7
  },
  {
    name: 'State of a component',
    exercises: 14
  }];

  return (
    <>
      <Header course={course}/>
      <Content part1={parts[0].name} part2={parts[1].name} part3={parts[2].name}  exercises1={parts[0].exercises}  exercises2={parts[1].exercises}  exercises3={parts[2].exercises}/>
      <Total exercises1={parts[0].exercises} exercises2={parts[1].exercises}  exercises3={parts[2].exercises}/>
    </>
  )
}

export default App
